mapper.select:
SELECT

FROM tb_menu m


        AND `name` LIKE CONCAT('%', #{menuQuery.name}, '%')


        AND status = #{menuQuery.status}


        AND create_time BETWEEN #{menuQuery.dataRange[0]} AND #{menuQuery.dataRange[1]}

SELECT DISTINCT m.id AS id, m.`name` AS name, m.parent_id AS parent_id, m.`path` AS path, m.type AS type, m.component AS component, m.is_cache AS is_cache, m.is_link AS is_link, m.status AS status, m.visible AS visible, m.icon AS icon, m.`order` AS `order`, m.create_time AS create_time, m.update_time AS update_time FROM tb_menu m INNER JOIN tb_role r ON r.mark = #{roleMark} INNER JOIN tb_menu_role mr ON mr.role_id = r.id AND m.id = mr.menu_id
AND m.`name` LIKE CONCAT('%', #{menuQuery.name}, '%')
AND m.status = #{menuQuery.status}
AND m.create_time BETWEEN #{menuQuery.dataRange[0]} AND #{menuQuery.dataRange[1]}
